if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/xampp/htdocs/AOS/GreenMovil/aos-greenmovil-backend-lambdas-nodejs/node_modules/.pnpm/velocityjs@2.0.6/node_modules/velocityjs/bin/node_modules:/mnt/e/xampp/htdocs/AOS/GreenMovil/aos-greenmovil-backend-lambdas-nodejs/node_modules/.pnpm/velocityjs@2.0.6/node_modules/velocityjs/node_modules:/mnt/e/xampp/htdocs/AOS/GreenMovil/aos-greenmovil-backend-lambdas-nodejs/node_modules/.pnpm/velocityjs@2.0.6/node_modules:/mnt/e/xampp/htdocs/AOS/GreenMovil/aos-greenmovil-backend-lambdas-nodejs/node_modules/.pnpm/node_modules"
